import { useEffect, useState } from 'react';
import React from 'react'
import classes from './AvailableMeals.module.css'
import Card from '../UI/Card';
import MealItem from './MealItems/MealItem';

const AvailableMeals = () => {
  const [meals, setMeals] = useState([])
  const [isLoading, setIsLoading] = useState(true)
  const [httpsError, setHttpError] = useState();
  
  useEffect(() =>{
   const fetchMeals = async () => 
    { 
      const response = await fetch('https://react-http-f0079-default-rtdb.firebaseio.com/Meals.json');
      
      if ( !response.ok ){
          throw new Error('Something went wrong!');
      }

      const responseData = await response.json();


      const loadedMeals = [];

      for (const key in responseData)
      {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price
        });
      }

      setMeals(loadedMeals);
      setIsLoading(false)
    };
    
      fetchMeals().catch(error =>{
        setIsLoading(false);
        setHttpError(error.message);
      })
    
  }, []);

  if(isLoading)
  {
    return <section className={classes.MealsLoading}>Loading...</section>
  }

  if(httpsError){
    return <section className={classes.MealsError}><p>{httpsError}</p></section>
  }

  const mealsList = meals.map(meal => 
  <MealItem 
  key ={meal.id}
  id = {meal.id}
  name = {meal.name}
  description = {meal.description}
  price = {meal.price}/>);
  
    return (
    <section className={classes.meals}>
      <Card><ul>{mealsList}</ul></Card></section>
  )
}

export default AvailableMeals
